import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import 'chart.js/auto';
import './styles.css';

function App() {
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [items, setItems] = useState([]);
  const [tagCount, setTagCount] = useState(0);
  const [form, setForm] = useState({ name: '', quantity: '', price: '', category: '', tags: '' });
  const [editingId, setEditingId] = useState(null);
  const [search, setSearch] = useState('');
  const [visibleChartId, setVisibleChartId] = useState(null);
  const [darkMode, setDarkMode] = useState(true);
  const [showDashboard, setShowDashboard] = useState(false);

  const toggleTheme = () => {
    setDarkMode(prev => !prev);
  };

  const fetchItems = async () => {
    try {
      const res = await axios.get('/api/items');
      if (Array.isArray(res.data)) {
        setItems(res.data);
      } else {
        setItems([]);
      }
    } catch {
      setItems([]);
    }
  };

  useEffect(() => {
    fetchItems();
  }, []);

  useEffect(() => {
    const fetchSearchResults = async () => {
      if (search.trim() === '') {
        fetchItems();
        return;
      }
      try {
        const res = await axios.get(`/search?q=${search}`);
        if (Array.isArray(res.data)) {
          setItems(res.data);
        } else {
          setItems([]);
        }
      } catch {
        setItems([]);
      }
    };
    fetchSearchResults();
  }, [search]);

  const handleChange = e => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });

    if (name === 'tags') {
      const count = value
        .split(';')
        .map(t => t.trim())
        .filter(Boolean).length;
      setTagCount(count);
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const tagsArray = form.tags.split(';').map(tag => tag.trim()).filter(Boolean);
    const payload = { ...form, tags: tagsArray };
    if (editingId) {
      await axios.put(`/api/items/${editingId}`, payload);
    } else {
      await axios.post('/api/items', payload);
    }
    setForm({ name: '', quantity: '', price: '', category: '', tags: '' });
    setTagCount(0);
    setEditingId(null);
    fetchItems();
  };

  const handleEdit = item => {
    setForm({
      name: item.name,
      quantity: item.quantity,
      price: item.price,
      category: item.category,
      tags: item.tags.join('; ')
    });
    setTagCount(item.tags.length);
    setEditingId(item._id);
  };

  const handleDelete = async id => {
    await axios.delete(`/api/items/${id}`);
    fetchItems();
  };

  const handleExport = () => {
    const csvData = items.map(({ _id, ...rest }) => ({ ...rest, tags: rest.tags.join(';') }));
    const blob = new Blob([
      Object.keys(csvData[0]).join(',') + '\n' +
      csvData.map(row => Object.values(row).join(',')).join('\n')
    ], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'inventory.csv';
    a.click();
  };

  const handleImport = async e => {
    const file = e.target.files[0];
    const formData = new FormData();
    formData.append('file', file);
    await axios.post('/api/items/import', formData);
    fetchItems();
  };

  const toggleChart = id => {
    setVisibleChartId(prev => (prev === id ? null : id));
  };

  const uniqueCategories = ['All', ...new Set(items.map(item => item.category))];

  return (
    <div className={`container ${darkMode ? 'dark' : 'light'}`}>
      <button className="theme-toggle" onClick={toggleTheme}>
        {darkMode ? 'Light Mode' : 'Dark Mode'}
      </button>
      <h1>Inventory Manager</h1>
      <input className="search" placeholder="Search by name..." value={search} onChange={e => setSearch(e.target.value)} />
      <form onSubmit={handleSubmit} className="form">
        <input name="name" placeholder="Name" value={form.name} onChange={handleChange} required />
        <input name="quantity" type="number" placeholder="Quantity" value={form.quantity} onChange={handleChange} required />
        <input name="price" type="number" placeholder="Price" value={form.price} onChange={handleChange} required />
        <input name="category" placeholder="Category" value={form.category} onChange={handleChange} required />
        <input name="tags" placeholder="Tags (e.g. tech;blue)" value={form.tags} onChange={handleChange} />
        <p className="tag-count">
          {tagCount} tag{tagCount !== 1 ? 's' : ''} 
          {tagCount > 5 && <span style={{ color: 'red' }}> (Limit: 5!)</span>}
        </p>
        <button type="submit">{editingId ? 'Update' : 'Add'} Item</button>
        <button type="button" onClick={() => {
          setForm({ name: '', quantity: '', price: '', category: '', tags: '' });
          setTagCount(0);
          setEditingId(null);
        }}>
          Clear Form
        </button>
      </form>

      <div className="controls">
        <button onClick={handleExport}>Export CSV</button>
        <label className="import-label">Import CSV
          <input type="file" accept=".csv" onChange={handleImport} hidden />
        </label>
      </div>

      <div className="category-filter">
        <label>Filter by Category: </label>
        <select value={selectedCategory} onChange={e => setSelectedCategory(e.target.value)}>
          {uniqueCategories.map(cat => (
            <option key={cat} value={cat}>{cat}</option>
          ))}
        </select>
      </div>

      <button className="dashboard-toggle" onClick={() => setShowDashboard(prev => !prev)}>
  {showDashboard ? 'Hide Dashboard ðŸ“‰' : 'Show Dashboard ðŸ“Š'}
</button>


      {showDashboard && (
        <div className="dashboard">
          <div className="stat-box">
            <strong>Total Items:</strong> {items.length}
          </div>
          <div className="stat-box">
            <strong>Total Categories:</strong> {new Set(items.map(item => item.category)).size}
          </div>
          <div className="stat-box">
            <strong>Low Stock:</strong> {items.filter(item => item.quantity < 5).length}
          </div>
          <div className="stat-box">
            <strong>Total Value:</strong> ${items.reduce((total, item) => total + (item.quantity * item.price), 0).toFixed(2)}
          </div>
        </div>
      )}

      <ul className="item-list">
        {items
          .filter(item => selectedCategory === 'All' || item.category === selectedCategory)
          .map(item => (
            <li key={item._id} className={`item ${item.quantity < 5 ? 'low-stock' : ''}`}>
              <div>
                <strong>{item.name}</strong> ({item.category})<br />
                Qty: {item.quantity} - ${item.price.toFixed(2)}<br />
                <em>Tags:</em> {item.tags.join(', ')}<br />
                <button onClick={() => toggleChart(item._id)}>
                  {visibleChartId === item._id ? 'Hide' : 'View'} Graph
                </button>
                {visibleChartId === item._id && (
                  <Bar
                    data={{
                      labels: [
                        ...(item.history || []).map(h =>
                          new Date(h.timestamp).toLocaleString()
                        ),
                        'Now'
                      ],
                      datasets: [
                        {
                          label: `${item.name} Quantity Over Time`,
                          data: [
                            ...(item.history || []).map(h => h.quantity),
                            item.quantity
                          ],
                          backgroundColor: 'rgba(75,192,192,0.6)'
                        }
                      ]
                    }}
                  />
                )}
              </div>
              <span className="actions">
                <button onClick={() => handleEdit(item)}>Edit</button>
                <button onClick={() => handleDelete(item._id)}>Delete</button>
              </span>
            </li>
          ))}
      </ul>
    </div>
  );
}

export default App;
