import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend
} from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend);

function DashboardPage() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    axios.get('/api/items')
      .then(res => {
        if (Array.isArray(res.data)) {
          setItems(res.data);
        } else {
          setItems([]);
        }
      })
      .catch(() => setItems([]));
  }, []);

  const totalItems = items.length;
  const categories = [...new Set(items.map(item => item.category))];
  const lowStock = items.filter(item => item.quantity < 5);
  const totalValue = items.reduce((acc, item) => acc + item.quantity * item.price, 0).toFixed(2);

  const categoryData = {
    labels: categories,
    datasets: [
      {
        label: 'Category Distribution',
        data: categories.map(cat => items.filter(item => item.category === cat).length),
        backgroundColor: [
          '#6c63ff',
          '#00c9a7',
          '#ff6b6b',
          '#ffa500',
          '#2d98da'
        ],
        borderWidth: 1
      }
    ]
  };

  const mostStocked = [...items].sort((a, b) => b.quantity - a.quantity).slice(0, 3);

  return (
    <main className="container">
      <h2>üìä Inventory Overview</h2>
      <div className="dashboard">
        <div className="stat-box">üì¶ Total Items: {totalItems}</div>
        <div className="stat-box">üìÇ Categories: {categories.length}</div>
        <div className="stat-box">‚ö†Ô∏è Low Stock: {lowStock.length}</div>
        <div className="stat-box">üí∞ Inventory Value: ${totalValue}</div>
      </div>

      <div className="dashboard-section">
        <h3>üìä Category Breakdown</h3>
        <div style={{ maxWidth: '300px', margin: '1.5rem auto' }}>
          <Pie data={categoryData} />
        </div>
      </div>

      <div className="dashboard-section">
        <h3>üèÜ Top 3 Stocked Items</h3>
        <ul style={{ paddingLeft: '1.2rem', lineHeight: '1.8' }}>
          {mostStocked.map((item, index) => (
            <li key={item._id}>
              {['ü•á', 'ü•à', 'ü•â'][index]} <strong>{item.name}</strong> (Qty: {item.quantity})
            </li>
          ))}
        </ul>
      </div>

      <div className="dashboard-section">
        <h3>‚ö†Ô∏è Low Stock Items</h3>
        <ul style={{ paddingLeft: '1.2rem', lineHeight: '1.8' }}>
          {lowStock.map(item => (
            <li key={item._id}>
              <strong>{item.name}</strong> (Qty: {item.quantity})
            </li>
          ))}
        </ul>
      </div>
    </main>
  );
}

export default DashboardPage;
