import React, { useEffect, useState } from 'react';
import axios from 'axios';

function DashboardPage() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    axios.get('/api/items')
      .then(res => {
        if (Array.isArray(res.data)) {
          setItems(res.data);
        } else {
          setItems([]);
        }
      })
      .catch(() => setItems([]));
  }, []);

  return (
    <main className="container">
      <div className="dashboard">
        <div className="stat-box">ðŸ“¦ Total Items: {items.length}</div>
        <div className="stat-box">ðŸ“‚ Categories: {new Set(items.map(i => i.category)).size}</div>
        <div className="stat-box">âš ï¸ Low Stock: {items.filter(i => i.quantity < 5).length}</div>
        <div className="stat-box">ðŸ’° Total Value: ${items.reduce((acc, item) => acc + item.quantity * item.price, 0).toFixed(2)}</div>
      </div>
    </main>
  );
}

export default DashboardPage;
